import React from 'react'

class TaskForm extends React.Component {
    state = {
        text: ''
    }

    handleOnChange = (event) => {
        this.setState({ text: event.target.value })
    }

    handleOnSubmit = (e) => {
        e.preventDefault()
        console.log("inside handle on submit")
    }

    render() {
        return (
            <form className="new-task-form" onSubmit={this.handleOnSubmit}>
                <input type="text" placeholder="New task details" onChange={this.handleOnChange} value={this.state.text} />
                <select>
                    <option>Code</option>
                    <option>Food</option>
                    <option>Money</option>
                    <option>Misc</option>
                </select>
                <input type="submit" value="Add task" />
            </form>
        )
    }
}

export default TaskForm
