import React from 'react';
import Task from '../components/Task'
import TaskForm from '../components/TaskForm'

class TasksContainer extends React.Component {
    render() {
        const { tasks, stretchFeatures } = this.props

        return (
            <div className="tasks">
                <h5>Tasks</h5>
                {(stretchFeatures) ? <TaskForm /> : null}
                {tasks.map(task => <Task key={task.text} task={task} stretchFeatures={stretchFeatures} />)}
            </div>
        )
    }
}

export default TasksContainer;
