import React from 'react'

class TaskForm extends React.Component {
    state = {
        text: ''
    }

    render() {
        return (
            <form className="new-task-form">
                <input type="text" placeholder="New task details" value={this.state.text} />

            </form>
        )
    }
}

export default TaskForm
