import React from 'react'

const Task = ({ task, stretchFeatures }) => {
    return (
        <div className='task'>
            <div className='label'>{task.category}</div>
            <div className='text'>{task.text}</div>
            { stretchFeatures ? <button className='delete'>X</button> : null}
        </div>
    )
}

export default Task
