import React from 'react';
import './App.css';
import { CATEGORIES } from './data';
import CategoriesContainer from './containers/CategoriesContainer';
import TasksContainer from './containers/TasksContainer';

class App extends React.Component {
  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    selectedCategory: 'All'
  }

  selectCategory = (selectedCategory) => {
    this.setState({ selectedCategory })
  }

  filteredTasks = (selectedCategory) => {
    if (selectedCategory === 'All') {
      return this.state.tasks
    } else {
      return this.state.tasks.filter(task => task.category === selectedCategory)
    }
  }

  render() {

    return (
      <div className="App">
        <button className="toggle off" onClick={() => console.log('Clicked!')}>Stretch features disabled</button>

        <h2>My tasks</h2>
        <CategoriesContainer categories={CATEGORIES} selectCategory={this.selectCategory} selectedCategory={this.state.selectedCategory} />
        <TasksContainer tasks={this.filteredTasks(this.state.selectedCategory)} />
      </div>
    );
  }
}

export default App;
